//skillgraph competencies
function LoadSkillgraph() {

$(".skillData").each(function(index, element) {
    // element == this
    var mydata = $(element).data();
    var cnt = 0;

    //recursive call with a time delay so user can see graph draw.
    function go() {
        if (cnt++ < mydata['percent']) {
            setTimeout(go, 10);
        }
        $(element).css('width', cnt + '%');
    }
    go();
});
}

$(document).ready(function(){
  $('#about').css('opacity', 0);

  $('#about').waypoint(function() {
      $('#about').addClass('fadeIn');
  }, { offset: '18%' });


  $('#competencies').waypoint(function() {
      LoadSkillgraph();
      this.destroy();
  }, { offset: '25%' });

  $('#ps-competencies').css('opacity', 0);
  $('.progress-bar').each(function(){
    $(this).removeClass();
  });

  $('#ps-competencies').waypoint(function() {
      $('#ps-competencies').addClass('fadeIn');
      $('#ps-right').addClass('progress-bar');
      $('#ps-left').addClass('progress-bar');
      $('#ps-right2').addClass('progress-bar');
      $('#ps-left2').addClass('progress-bar');
      $('#ps-right3').addClass('progress-bar');
      $('#ps-left3').addClass('progress-bar');
      $('#ps-right4').addClass('progress-bar');
      $('#ps-left4').addClass('progress-bar');
     }
  , { offset: '40%' });


});
